element.Formula:
altri=context.getItem('anagrafica_soggetti',[])
if altri and len(altri)>0:
    return ' e altri'
else:
    return ''

destinatari = []
if context.getItem('progettista_pec'):
    destinatari.append(context.getItem('progettista_pec'))
if context.getItem('tecnico_pec'):
    destinatari.append(context.getItem('tecnico_pec'))
if context.getItem('delegato_pec'):
    destinatari.append(context.getItem('delegato_pec'))    
if not context.getItem('consenso_pec') or context.getItem('iol_tipo_app')=='aua':
    if context.getItem('fisica_pec'):
        destinatari.append(context.getItem('fisica_pec'))    

return destinatari
